<script>
  export let field;
  export let type = field.input_type || "text";
  export let value;
  const otherprops = { ...$$props };
  delete otherprops.type;
  delete otherprops.field;
  delete otherprops.value;

  import "bootstrap/dist/css/bootstrap.min.css";
</script>

<div class="form-group">
  <label for={`inp-${field.key}`}>{field.label}</label>
  <slot>
    {#if type == 'text'}
      <input
        bind:value
        type="text"
        id={`inp-${field.key}`}
        {...otherprops}
        class="form-control" />
    {:else if type == 'textarea'}
      <textarea
        id={`inp-${field.key}`}
        {...otherprops}
        bind:value
        class="form-control" />
    {:else if type == 'date'}
      <input
        bind:value
        type="date"
        id={`inp-${field.key}`}
        {...otherprops}
        class="form-control" />
    {:else if type == 'phone'}
      <input
        bind:value
        type="phone"
        id={`inp-${field.key}`}
        {...otherprops}
        class="form-control" />
    {:else}
      <p>Unsupported input type: {type}</p>
    {/if}
  </slot>
</div>
