<script>
  export let name;
  export let plural = `${name}s`;
  export let fields;
  export let records;
  export let record = {};
  export let rest_resource;
  
  import { goto } from "@sapper/app";

  function editRecord(r) {
    record = r;
  }
  function deleteRecord(r) {
    goto(`${rest_resource}/${r._id}/delete`);
  }
</script>

<style>
.actions .fa {
  cursor: pointer;
}
</style>
<slot name="table">
  <table class="table">
    <thead>
      <tr>
        {#each fields as field}
          <th>{field.label}</th>
        {/each}
        <th>Actions</th>
      </tr>
    </thead>
    <tbody>
      {#each records as rec}
        <tr>
          {#each fields as field}
            <td>{rec[field.key]}</td>
          {/each}
          <td class="actions">
            <i class="fa fa-edit" on:click={() => editRecord(rec)} />
            <i class="fa fa-trash" on:click={() => deleteRecord(rec)} />
          </td>
        </tr>
      {/each}
    </tbody>
  </table>
</slot>
